const burgerButton=document.querySelector(".nav__burger-menu"),closeButton=document.querySelector(".nav__close-menu"),page=document.querySelector(".page"),pageMenu=document.querySelector(".page__menu");burgerButton.addEventListener("click",(()=>{page.classList.add("page--with-menu"),pageMenu.classList.add("page__menu--visible")})),closeButton.addEventListener("click",(()=>{page.classList.remove("page--with-menu"),pageMenu.classList.remove("page__menu--visible")}));